mapper.update:
UPDATE 	USER_ACCOUNT SET
		USER_NAME 			= #{user_name}
		, USER_EMAIL 		= #{user_email}
		, USER_ADDR1 		= #{user_addr1}
		, USER_ADDR2 		= #{user_addr2}
		, USER_TEL 			= #{user_tel}
		, USER_GENDER 		= #{user_gender}
		, USER_BIRTHDAY 	= #{user_birthday}
WHERE  user_cate_no = 2 AND user_no = #{user_no}

mapper.insert:
INSERT INTO USER_ACCOUNT (
	USER_NO 
	, USER_ID
	, USER_PASS 
	, USER_NAME 
	, USER_EMAIL 
	, USER_ADDR1
	, USER_ADDR2
	, USER_TEL 
	, USER_GENDER 
	, USER_BIRTHDAY
	, USER_SIGNUPDATE
	, USER_CATE_NO 
) VALUES (
	seq_user_no.nextval
	, #{user_id}
	, #{user_pass} 
	, #{user_name}
	, #{user_email}
	, #{user_addr1}
	, #{user_addr2}
	, #{user_tel} 
	, #{user_gender}
	, #{user_birthday}
	, sysdate
	, 2
)
